mapper.select:
        SELECT


--         级联表的字段
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail

        FROM zufang  a
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and a.zufang_name like CONCAT('%',#{params.zufangName},'%')


                and a.zufang_uuid_number like CONCAT('%',#{params.zufangUuidNumber},'%')


                and a.zufang_types = #{params.zufangTypes}


                and a.quyu_types = #{params.quyuTypes}


                and a.zufang_xiaoqu like CONCAT('%',#{params.zufangXiaoqu},'%')


                and a.zufang_weizhi like CONCAT('%',#{params.zufangWeizhi},'%')


                  and a.zufang_mianji >= #{params.zufangMianjiStart}   


                  and a.zufang_mianji <= #{params.zufangMianjiEnd}   


                  and a.zufang_old_money >= #{params.zufangOldMoneyStart}   


                  and a.zufang_old_money <= #{params.zufangOldMoneyEnd}   


                  and a.zufang_new_money >= #{params.zufangNewMoneyStart}   


                  and a.zufang_new_money <= #{params.zufangNewMoneyEnd}   


                  and a.zufang_clicknum >= #{params.zufangClicknumStart}   


                  and a.zufang_clicknum <= #{params.zufangClicknumEnd}   


                and a.zufang_clicknum = #{params.zufangClicknum}


                and a.zufang_content like CONCAT('%',#{params.zufangContent},'%')


                and a.zufang_zhuangtai_types = #{params.zufangZhuangtaiTypes}


                and a.shangxia_types = #{params.shangxiaTypes}


                  and a.zufang_delete >= #{params.zufangDeleteStart}   


                  and a.zufang_delete <= #{params.zufangDeleteEnd}   


                and a.zufang_delete = #{params.zufangDelete}




                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')



        order by a.${params.orderBy} desc
